import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getFormById } from './formapi';

const segments = [
  { name: 'Sub - Part Dimensiol', order: 1 },
  { name: 'Final Part Measurement', order: 2 },
  { name: 'Paint Report', order: 3 },
  { name: 'Qoating Report', order: 4 },
  { name: 'Product Packing Standart', order: 5 },
  { name: 'Loading Check', order: 6 },
];

const FormEdit = () => {
  const { id } = useParams();
  const [form, setForm] = useState(null);
  const [activeSegment, setActiveSegment] = useState(1);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const formData = await getFormById(id);
        setForm(formData);
      } catch (error) {
        console.error('Error fetching form:', error);
      }
    };

    fetchData();
  }, [id]);

  const createTable = (data) => {
    const headers = [
      'İsim',
      'Teknik Çizim Numarası',
      'Kullanılan Aletler',
      'Açıklama',
      'Gerçek Boyut',
      'Alt Tolerans',
      'Üst Tolerans',
    ];

    const finalPartMeasurementStep = data.steps.find(
      (step) => step.name === 'Final Part Measurement'
    );

    if (!finalPartMeasurementStep) return null;

    return (
      <table className="measurement-table">
        <thead>
          <tr>
            {headers.map((header, index) => (
              <th key={index}>{header}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {finalPartMeasurementStep.substeps.map((substep) => (
            <tr key={substep.id}>
              <td>
                <input type="text" defaultValue={substep.name} />
              </td>
              <td>
                <input type="text" defaultValue={substep.technical_drawing_numbering} />
              </td>
              <td>
                <input type="text" defaultValue={substep.tools} />
              </td>
              <td>
                <input type="text" defaultValue={substep.description} />
              </td>
              <td>
                <input type="text" defaultValue={substep.actual_dimension} />
              </td>
              <td>
                <input type="text" defaultValue={substep.lower_tolerance} />
              </td>
              <td>
                <input type="text" defaultValue={substep.upper_tolerance} />
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    );
  };


  const handleSegmentClick = (order) => {
    setActiveSegment(order);
  };

  const renderSegmentContent = () => {
    switch (activeSegment) {
      case 1:
        return renderSubPartDimensiol();
      case 2:
        return renderFinalPartMeasurement();
      case 3:
        return renderPaintReport();
      case 4:
        return renderQoatingReport();
      default:
        return null;
    }
  };

  const renderSubPartDimensiol = () => {
    return (
      <div>
        {form.steps[0].substeps.map((substep) => (
          <div key={substep.id}>
            <h3>{substep.name}</h3>
            <p>{substep.description}</p>
            <p>{`Actual dimension: ${substep.actual_dimension}`}</p>
            <p>{`Tolerances: ${substep.lower_tolerance}-${substep.upper_tolerance}`}</p>
            <img src={substep.example_visual_url} alt={substep.name} />
          </div>
        ))}
      </div>
    );
  };

  const renderFinalPartMeasurement = () => {
    if (!form) return null;

    return <div>{createTable(form)}</div>;
  };

  
  const renderPaintReport = () => {
    return (
      <div>
        {form.steps[2].substeps.map((substep) => (
          <div key={substep.id}>
            <h3>{substep.name}</h3>
            <p>{substep.description}</p>
            <p>{`Actual dimension: ${substep.actual_dimension}`}</p>
            <p>{`Tolerances: ${substep.lower_tolerance}-${substep.upper_tolerance}`}</p>
            <img src={substep.example_visual_url} alt={substep.name} />
          </div>
        ))}
      </div>
    );
  };
  
  const renderQoatingReport = () => {
    return (
      <div>
        {form.steps[3].substeps.map((substep) => (
          <div key={substep.id}>
            <h3>{substep.name}</h3>
            <p>{substep.description}</p>
            <p>{`Actual dimension: ${substep.actual_dimension}`}</p>
            <p>{`Tolerances: ${substep.lower_tolerance}-${substep.upper_tolerance}`}</p>
            <img src={substep.example_visual_url} alt={substep.name} />
          </div>
        ))}
      </div>
    );
  };
  return (
    <div>
      <h1>ITP Formu</h1>
      {form ? (
        <div>
          <h2>Product Name: {form.product_name || form.product?.name}</h2>
          <h2>Vendor Name: {form.vendor_name || form.vendor?.name}</h2>
            <div className="segments">
                {segments.map((segment) => (
                <button
                    key={segment.order}
                    onClick={() => handleSegmentClick(segment.order)}
                    className={activeSegment === segment.order ? 'active' : ''}
                >
                    {segment.name}
                </button>
                ))}
            </div>
            <div className="segment-content">{renderSegmentContent()}</div>
        </div>
      ) : (
        <p>Form yükleniyor...</p>
      )}
    </div>
  );
};

export default FormEdit;
