import React from 'react';
import { useNavigate } from 'react-router-dom';

const CreateWorkOrder = () => {
  const navigate = useNavigate();
  const [workData, setWorkData] = useState({
    order_number: "",
    project_number: "",
    vendor_id: "",
    customer_id: "",
    quality_responsible_id: "",
    inspector_id: "",
    foreman_id: "",
    work_type: "",
    state: "",
    status: "",
    creator_id: "",
    creation_date: "",
    product_id: "",
  });

  const handleChange = (e) => {
    setWorkData({ ...workData, [e.target.name]: e.target.value });
  };
  
  const handleSave = async (e) => {
    e.preventDefault();
    try {
      const createdWork = await createWork(workData);
      const workStepData = {
        work_id: createdWork.id,
        step_name: "QR Control",
        timestamp: new Date().toISOString(),
        state: "QR Control",
        status: "Open",
      };
      await createWorkStep(workStepData);
      navigate('/qr-control');
    } catch (error) {
      console.error('Error creating work and workstep:', error);
    }
  };
  

  return (
    <div>
      <h2>Create Work Order</h2>
      <form>
        <div className="form-group">
          <label htmlFor="title">Title</label>
          <input type="text" name="title" id="title" className="form-control" required />
        </div>
        <div className="form-group">
          <label htmlFor="description">Description</label>
          <textarea name="description" id="description" className="form-control" required></textarea>
        </div>
        <button onClick={handleSave} className="btn btn-primary">
          Save
        </button>
        <button onClick={handleSend} className="btn btn-success">
          Send
        </button>
      </form>
    </div>
  );
};

export default CreateWorkOrder;
