// EditableTable.js
import React, { useState } from 'react';
import './ProductPage.css';

const initialData = [
  // Örnek veriler
  {
    name: 'Örnek 1',
    odooid: 'ODOO001',
    customer: 'Müşteri A',
    technicalDrawing: 'https://example.com/pdf1.pdf',
    guide: 'https://example.com/guide1.jpg',
  },
  // Daha fazla örnek veri ekleyebilirsiniz...
];

const EditableTable = () => {
  const [data, setData] = useState(initialData);
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const filteredData = data.filter((item) =>
    Object.values(item).some(
      (value) =>
        value &&
        value.toString().toLowerCase().includes(searchTerm.toLowerCase())
    )
  );

  const sortedData = filteredData.sort((a, b) => a.name.localeCompare(b.name));

  return (
    <div className="editable-table-container">
      <div className="header-container">
        <h1>Editlenebilir Tablo</h1>
        <input
          type="text"
          placeholder="Arama..."
          value={searchTerm}
          onChange={handleSearchChange}
        />
      </div>
      <table className="editable-table">
        <thead>
          <tr>
            <th>İsim</th>
            <th>Odooid</th>
            <th>Müşteri</th>
            <th>Teknik Çizim (PDF)</th>
            <th>Kılavuz (PDF, JPG)</th>
          </tr>
        </thead>
        <tbody>
          {sortedData.map((item, index) => (
            <tr key={index}>
              {Object.keys(item).map((key) => (
                <td key={key}>
                  <input
                    type="text"
                    value={item[key]}
                    onChange={(event) => {
                      const newData = [...data];
                      newData[index][key] = event.target.value;
                      setData(newData);
                    }}
                  />
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default EditableTable;
