import React from 'react';
import './BarChart.css';

const BarChart = ({ data }) => {
  const maxValue = Math.max(...data.map((item) => item.iş));
  const scale = 100 / maxValue;

  return (
    <div className="bar-chart">
      {data.map((item, index) => (
        <div key={index} className="bar-chart__column">
          <div
            className="bar-chart__bar"
            style={{
              height: `${item.iş * scale}%`,
            }}
          >
            <span className="bar-chart__value">{item.iş}</span>
          </div>
          <span className="bar-chart__label">{item.name}</span>
        </div>
      ))}
    </div>
  );
};

export default BarChart;
