import React, { useState, useRef, useEffect } from 'react';
import { updateImageStatus } from './ImagePopupApi';
import './ImagePopup.css';

export default function ImagePopup({ onClose, imageList }) {
  const popupContentRef = useRef(null);

  useEffect(() => {
    function handleClickOutside(event) {
      if (popupContentRef.current && !popupContentRef.current.contains(event.target)) {
        onClose();
      }
    }

    document.addEventListener('mousedown', handleClickOutside);

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [onClose]);

  const handleApproval = async (imageId, status) => {
    try {
      const response = await updateImageStatus(imageId, status);
      console.log(response);
    } catch (error) {
      console.error('Error updating image status:', error);
    }
  }

  return (
    <div className="image-popup">
      <div className="popup-content" ref={popupContentRef}>
        <div className="image-container">
          {imageList.map((image, index) => (
            <div key={index}>
              <img src={image.image_url} alt={`Resim ${index}`} className="popup-image" />
              <div>
                <button onClick={() => handleApproval(image.id, 'approved')}>Onayla</button>
                <button onClick={() => handleApproval(image.id, 'rejected')}>Reddet</button>
              </div>
            </div>
          ))}
        </div>
        <div className="close-button-container">
          <button className="popup-close" onClick={onClose}>
            Kapat
          </button>
        </div>
      </div>
    </div>
  );
}
