import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Certificate = ({ workId, productId, stepId }) => {
  const [file, setFile] = useState(null);
  const navigate = useNavigate();

  const handleSend = async () => {
    if (file) {
      const formData = new FormData();
      formData.append('certificate_file', file);
      formData.append('work_id', workId);
      formData.append('product_id', productId);
      formData.append('step_id', stepId);

      try {
        await axios.post('/certificates', formData, {
          headers: { 'Content-Type': 'multipart/form-data' },
        });
      } catch (error) {
        console.error(error);
      }
    }

    navigate('/workorders');
  };

  const handleContinueWithoutSending = () => {
    navigate('/workorders');
  };

  return (
    <div>
      <h2>Certificate</h2>
      <input type="file" onChange={(e) => setFile(e.target.files[0])} />
      <button onClick={handleSend} className="btn btn-primary">
        Gönder
      </button>
      <button onClick={handleContinueWithoutSending} className="btn btn-secondary">
        Göndermeden Devam Et
      </button>
    </div>
  );
};

export default Certificate;
