import React, { useState } from 'react';
import './gallery.css';


const images = [
  'https://picsum.photos/id/10/800/600',
  'https://picsum.photos/id/20/800/600',
  'https://picsum.photos/id/30/800/600'
];

const Gallery = ({ images = [] }) => {
  const [selectedImage, setSelectedImage] = useState(null);

  const openImage = (url) => {
    setSelectedImage(url);
  };

  const closeImage = () => {
    setSelectedImage(null);
  };

  const changeImage = (direction) => {
    if (selectedImage) {
      const currentIndex = images.indexOf(selectedImage);
      if (direction === 'left' && currentIndex > 0) {
        setSelectedImage(images[currentIndex - 1]);
      } else if (direction === 'right' && currentIndex < images.length - 1) {
        setSelectedImage(images[currentIndex + 1]);
      }
    }
  };

  return (
    <div className="gallery-container">
      {images.map((url, index) => (
        <img
          key={index}
          src={url}
          alt={`Gallery Image ${index + 1}`}
          className="gallery-image"
          onClick={() => openImage(url)}
        />
      ))}
      {selectedImage && (
        <div className="lightbox" onClick={closeImage}>
          <span className="lightbox-prev" onClick={() => changeImage('left')}>&lt;</span>
          <img className="lightbox-image" src={selectedImage} alt="Selected" />
          <span className="lightbox-next" onClick={() => changeImage('right')}>&gt;</span>
        </div>
      )}
    </div>
  );
};

export default Gallery;
