import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getTransferByName } from './TransfersApi';
import './TransfersDetail.css';

const TransfersDetail = () => {
  const [transfer, setTransfer] = useState(null);
  const { name } = useParams();
  const actualName = name.replace(/-/g, ' ');

  useEffect(() => {
    getTransferByName(actualName)
      .then(data => setTransfer(data))
      .catch(err => console.error(err));
  }, [actualName]);

  if (!transfer) {
    return <div>Loading...</div>;
  }

  return (
    <div className="transfer-detail-container">
      <h1>{transfer.name}</h1>
      {/* Transfer ayrıntılarını burada görüntüleyin */}
    </div>
  );
};

export default TransfersDetail;
