import React, { useState, useEffect } from 'react';
import axios from 'axios';

const FormEdit = ({ match }) => {
  const { id } = match.params;
  const [form, setForm] = useState(null);

  useEffect(() => {
    axios.get(`/forms/${id}`).then((response) => {
      console.log('Server response:', response); // Bu satırı ekleyin
      setForm(response.data);
    });
  }, [id]);

  return (
    <div>
      <h1>Form Düzenleme: {id}</h1>
      {form ? (
        <div>
          <h2>Product Name: {form.product_name}</h2>
          <h2>Vendor Name: {form.vendor_name}</h2>
          {/* Burada form düzenleme alanlarınızı ve mantığınızı ekleyebilirsiniz */}
        </div>
      ) : (
        <p>Form yükleniyor...</p>
      )}
    </div>
  );
};

export default FormEdit;
