import React, { useState } from 'react';
import './Chart.css';

const Line = ({ x1, y1, x2, y2, color }) => (
  <line x1={x1} y1={y1} x2={x2} y2={y2} stroke={color} strokeWidth="2" />
);

const Circle = ({ cx, cy, r, fill, onMouseEnter, onMouseLeave }) => (
  <circle cx={cx} cy={cy} r={r} fill={fill} onMouseEnter={onMouseEnter} onMouseLeave={onMouseLeave} />
);

const Chart = ({ data }) => {
  const [hoverData, setHoverData] = useState(null);

  const width = 600;
  const height = 300;
  const margin = { top: 20, right: 20, bottom: 50, left: 60 };
  const chartWidth = width - margin.left - margin.right;
  const chartHeight = height - margin.top - margin.bottom;

  const maxValue = Math.max(...data.map(d => Math.max(d.iş, d.iptalEdilenİş)));
  const scaleY = value => (chartHeight * (1 - value / maxValue)) + margin.top;
  const scaleX = (value, length) => (chartWidth * (value / length)) + margin.left;

  return (
    <div className="chart" style={{ position: 'relative' }}>
      <svg width={width} height={height}>
        {/* ...Önceki kodlar... */}

        {/* Eksenler */}
        <line x1={margin.left} y1={margin.top} x2={margin.left} y2={height - margin.bottom} stroke="black" />
        <line x1={margin.left} y1={height - margin.bottom} x2={width - margin.right} y2={height - margin.bottom} stroke="black" />

        {/* Eksen ok uçları */}
        <path
          d={`M${margin.left - 5},${margin.top + 5} L${margin.left},${margin.top} L${margin.left + 5},${margin.top + 5}`}
          fill="black"
        />
        <path
          d={`M${width - margin.right - 5},${height - margin.bottom - 5} L${width - margin.right},${height - margin.bottom} L${width - margin.right - 5},${height - margin.bottom + 5}`}
          fill="black"
        />

        {/* ...Diğer kodlar... */}
      </svg>
      {hoverData && (
        <div
          className="chart-tooltip"
          style={{
            left: hoverData.x + 10,
            top: hoverData.y - 25,
          }}
        >
          <div>{hoverData.label}</div>
          <div>Değer: {hoverData.value}</div>
        </div>
      )}
    </div>
  );
};

export default Chart;
