import React, { useState } from 'react';
import './ProductSegment.css';

const ProductSegment = ({ product, steps }) => {
  const [showContent, setShowContent] = useState(false);

  const handleToggleContent = () => {
    setShowContent(!showContent);
  };

  return (
    <div className="product-segment">
      <button onClick={handleToggleContent} className={showContent ? 'active' : ''}>
        {product.product_name}
      </button>
      {showContent && (
        <div className="segment-content">
          <p>ID: {product.product_id}</p>
          <p>Vendor ID: {product.vendor_id}</p>
          <p>Vendor Name: {product.vendor_name}</p>
          <div className="steps-container">
            {steps.map((step, index) => (
              <div key={index} className="step">
                <h3>{step.name}</h3>
                <SubstepTable substeps={step.substeps} />
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

const SubstepTable = ({ substeps }) => {
    return (
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Teknik Çizim Numarası</th>
            <th>Araçlar</th>
            <th>Açıklama</th>
            <th>Gerçek Boyut</th>
            <th>Alt Tolerans</th>
            <th>Üst Tolerans</th>
          </tr>
        </thead>
        <tbody>
          {substeps.map((substep, index) => (
            <tr key={index}>
              <td>{substep.id}</td>
              <td>{substep.technical_drawing_numbering}</td>
              <td>{substep.tools}</td>
              <td>{substep.description}</td>
              <td>{substep.actual_dimension}</td>
              <td>{substep.lower_tolerance}</td>
              <td>{substep.upper_tolerance}</td>
            </tr>
          ))}
        </tbody>
      </table>
    );
  };

export default ProductSegment;
