import React, { useState, useRef, useEffect } from 'react';
import { updateImageStatus } from './ImagePopupApi';
import './ImagePopup.css';

export default function ImagePopup({ onClose, images }) {
  const popupContentRef = useRef(null);
  const [imageList, setImageList] = useState(images);

  useEffect(() => {
    function handleClickOutside(event) {
      if (popupContentRef.current && !popupContentRef.current.contains(event.target)) {
        onClose();
      }
    }

    document.addEventListener('mousedown', handleClickOutside);

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [onClose]);

  const handleApproval = async (imageId) => {
    try {
      const response = await updateImageStatus(imageId, 'approved');
      if (response) {
        setImageList(imageList.map(img => img.id === imageId ? {...img, status: 'approved'} : img));
      }
    } catch (error) {
      console.error('Error updating image status:', error);
    }
  };

  const handleRejection = async (imageId) => {
    try {
      const response = await updateImageStatus(imageId, 'rejected');
      if (response) {
        setImageList(imageList.map(img => img.id === imageId ? {...img, status: 'rejected'} : img));
      }
    } catch (error) {
      console.error('Error updating image status:', error);
    }
  };

  return (
    <div className="image-popup">
      <div className="popup-content" ref={popupContentRef}>
        <div className="image-container">
          {imageList.map((image, index) => (
            <div key={index} className={`image-item status-${image.status}`}>
              <img src={image.url} alt={`Resim ${index}`} className={`popup-image status-${image.status}`} />
              <div className="image-controls">
                <button className="btn btn-approve" onClick={() => handleApproval(image.id)}>Onayla</button>
                <button className="btn btn-reject" onClick={() => handleRejection(image.id)}>Reddet</button>
              </div>
            </div>
          ))}
        </div>
        <div className="close-button-container">
          <button className="popup-close" onClick={onClose}>
            Kapat
          </button>
        </div>
      </div>
    </div>
  );
}
