import React, { useState, useRef, useEffect } from 'react';
import './gallery.css';

const images = [
  'https://picsum.photos/id/10/800/600',
  'https://picsum.photos/id/20/800/600',
  'https://picsum.photos/id/30/800/600'
];

const Gallery = () => {
  const [currentImage, setCurrentImage] = useState(0);
  const [showModal, setShowModal] = useState(false);
  const modalRef = useRef();

  useEffect(() => {
    const handleOutsideClick = (event) => {
      if (modalRef.current && !modalRef.current.contains(event.target)) {
        setShowModal(false);
      }
    };

    window.addEventListener('click', handleOutsideClick);

    return () => {
      window.removeEventListener('click', handleOutsideClick);
    };
  }, []);

  const handlePrev = () => {
    setCurrentImage(currentImage === 0 ? images.length - 1 : currentImage - 1);
  };

  const handleNext = () => {
    setCurrentImage(currentImage === images.length - 1 ? 0 : currentImage + 1);
  };

  const handleClick = (index) => {
    setCurrentImage(index);
    setShowModal(true);
    console.log("t覺klamnd覺")
  };

  return (
    <div className="gallery-container">
      <div className="gallery-images">
        {images.map((image, index) => (
          <div key={index} onClick={() => handleClick(index)}>
            <img
              className="gallery-image"
              src={image}
              alt={`Image ${index}`}
            />
          </div>
        ))}
      </div>
      {showModal && (
        <div className="gallery-modal" ref={modalRef}>
          <img
            className="modal-image"
            src={images[currentImage]}
            alt={`Image ${currentImage}`}
          />
          <div className="modal-arrows">
            <div className="modal-arrow modal-arrow-left" onClick={handlePrev}>
              &lt;
            </div>
            <div className="modal-arrow modal-arrow-right" onClick={handleNext}>
              &gt;
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Gallery;
