import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getFormById } from './formapi';

const FormEdit = () => {
  const { id } = useParams();
  const [form, setForm] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const formData = await getFormById(id);
        setForm(formData);
      } catch (error) {
        console.error('Error fetching form:', error);
      }
    };

    fetchData();
  }, [id]);

  return (
    <div>
      <h1>ITP Formu</h1>
      {form ? (
        <div>
          <h2>Product Name: {form.product_name || form.product?.name}</h2>
          <h2>Vendor Name: {form.vendor_name || form.vendor?.name}</h2>
          {/* Burada form düzenleme alanlarınızı ve mantığınızı ekleyebilirsiniz */}
        </div>
      ) : (
        <p>Form yükleniyor...</p>
      )}
    </div>
  );
};

export default FormEdit;
