import React, { useState, useEffect } from "react";
import axios from "axios";

const Vendors = () => {
  const [vendors, setVendors] = useState([]);
  const [id, setId] = useState("");
  const [name, setName] = useState("");
  const [odooid, setOdooid] = useState("");

  useEffect(() => {
    fetchVendors();
  }, []);

  const fetchVendors = async () => {
    try {
      const response = await axios.get("localhost:3001/api/vendors");
      setVendors(response.data.data);
    } catch (error) {
      console.error("Tedarikçiler alınırken hata oluştu:", error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await axios.post("/api/vendors", { id, name, odooid });
      fetchVendors();
      setId("");
      setName("");
      setOdooid("");
    } catch (error) {
      console.error("Tedarikçi eklenirken hata oluştu:", error);
    }
  };

  return (
    <div className="vendor-table-container">
      <form onSubmit={handleSubmit}>
        <label htmlFor="id">ID:</label>
        <input
          type="text"
          id="id"
          value={id}
          onChange={(e) => setId(e.target.value)}
        />
        <label htmlFor="name">İsim:</label>
        <input
          type="text"
          id="name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <label htmlFor="odooid">Odooid:</label>
        <input
          type="text"
          id="odooid"
          value={odooid}
          onChange={(e) => setOdooid(e.target.value)}
        />
        <button type="submit">Tedarikçi Ekle</button>
      </form>
      <table className="vendor-table">
        <thead>
          <tr>
            <th>ID</th>
            <th>İsim</th>
            <th>Odooid</th>
          </tr>
        </thead>
        <tbody>
          {vendors.map((vendor) => (
            <tr key={vendor.id}>
              <td>{vendor.id}</td>
              <td>{vendor.name}</td>
              <td>{vendor.odooid}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Vendors;
