import React, { useState, useEffect } from 'react';
import { getProducts, addProduct, updateProduct, deleteProduct } from '..//..//api';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faSave, faPlus, faTrash } from '@fortawesome/free-solid-svg-icons';
import './ProductTable.css';

const ProductTable = () => {
  const [products, setProducts] = useState([]);
  const [addingProduct, setAddingProduct] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchField, setSearchField] = useState('');

  useEffect(() => {
    async function fetchProducts() {
      const data = await getProducts();
      setProducts(data);
    }

    fetchProducts();
  }, []);

  const handleAddProduct = async (newProduct) => {
    const addedProduct = await addProduct(newProduct);
    setProducts([...products, addedProduct]);
    setAddingProduct(false);
  };

  const handleUpdateProduct = async (id, updatedProduct) => {
    const updatedProductData = await updateProduct(id, updatedProduct);
    setProducts(
      products.map((product) =>
        product.id === id ? updatedProductData : product
      )
    );
  };

  const handleDeleteProduct = async (id) => {
    await deleteProduct(id);
    setProducts(products.filter((product) => product.id !== id));
  };

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleFieldChange = (event) => {
    setSearchField(event.target.value);
  };

  const filteredData = products.filter((product) => {
    if (!searchTerm || !searchField) return true;
    return product[searchField]
      .toString()
      .toLowerCase()
      .includes(searchTerm.toLowerCase());
  });

  const sortedData = filteredData.sort((a, b) => a.name.localeCompare(b.name));

  return (
    <div className="product-table-container">
      // ... (header-container kısmı)

      <table className="product-table">
        // ... (thead kısmı)

        <tbody>
          {addingProduct && (
            <tr>
              <td><input type="text" placeholder="İsim" /></td>
              <td><input type="text" placeholder="Odooid" /></td>
              <td><input type="text" placeholder="Müşteri" /></td>
              <td><input type="text" placeholder="Teknik Çizim (PDF)" /></td>
              <td><input type="text" placeholder="Kılavuz (PDF, JPG)" /></td>
              <td>
                <button
                  onClick={() => {
                    const newProduct = {
                      name: '',
                      odooid: '',
                      customer: '',
                      technicalDrawing: '',
                      guide: '',
                    };
                    handleAddProduct(newProduct);
                  }}
                >
                  <FontAwesomeIcon icon={faSave} />
                  {' Kaydet'}
                </button>
                <button onClick={() => setAddingProduct(false)}>
                  <FontAwesomeIcon icon={faTrash} />
                  {' İptal'}
                </button>
              </td>
            </tr>
          )}
          {sortedData.map((product, index) => (
// ... (Mevcut ürünlerin görüntülendiği tr kısmının devamı)

<tr key={index}>
  {Object.keys(product).map((key, fieldIndex) => (
    <td key={`${index}-${fieldIndex}`}>
      <input
        type="text"
        value={product[key]}
        onChange={(event) => {
          const newData = [...products];
          newData[index][key] = event.target.value;
          setProducts(newData);
        }}
      />
    </td>
  ))}
  <td>
    <button
      onClick={() => handleUpdateProduct(product.id, product)}
    >
      <FontAwesomeIcon icon={faSave} />
      {' Kaydet'}
    </button>
    <button
      onClick={() => handleDeleteProduct(product.id)}
    >
      <FontAwesomeIcon icon={faTrash} />
      {' Sil'}
    </button>
  </td>
</tr>
))}
        </tbody>
      </table>
    </div>
  );
};

export default ProductTable;
