import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { createWork, createWorkStep, getVendors, getCustomers, getUsers, getProducts, createWorkProduct } from './worksapi';
import "./CreateWorkOrder.css";

const CreateWorkOrder = () => {
  const navigate = useNavigate();
  const [workData, setWorkData] = useState({ order_number: "", project_number: "", vendor_id: "", customer_id: "", quality_responsible_id: "", inspector_id: "", foreman_id: "", work_type: "", state: "", status: "", creator_id: "", creation_date: "", });
  const [selectedProducts, setSelectedProducts] = useState([]);
  const [vendors, setVendors] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [qualityResponsibles, setQualityResponsibles] = useState([]);
  const [inspectors, setInspectors] = useState([]);
  const [foremen, setForemen] = useState([]);
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => { fetchVendors(); fetchCustomers(); fetchUsers(); fetchProducts(); }, []);
  const handleSearchChange = (e) => { setSearchTerm(e.target.value); };
  const filterProducts = () => searchTerm === "" ? products : products.filter((product) => product.name.toLowerCase().includes(searchTerm.toLowerCase()));
  const fetchData = async (fetchFunction, setStateFunction) => {
    try {
      const response = await fetchFunction();
      setStateFunction(response.data);
    } catch (error) {
      console.error(`Error fetching data:`, error);
    }
  };

  const fetchVendors = async () => { fetchData(getVendors, setVendors); };
  const fetchCustomers = async () => { fetchData(getCustomers, setCustomers); };
  const fetchUsers = async () => {
    try {
      const response = await getUsers();
      setQualityResponsibles(response.data.filter((user) => user.role === 'Quality Responsible'));
      setInspectors(response.data.filter((user) => user.role === 'Inspector'));
      setForemen(response.data.filter((user) => user.role === 'Foreman'));
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };
  const fetchProducts = async () => { fetchData(getProducts, setProducts); };

  const handleProductChange = (e) => { setSelectedProducts(Array.from(e.target.selectedOptions).map((option) => Number(option.value))); };
  const handleChange = (e) => { setWorkData({ ...workData, [e.target.name]: e.target.value }); };

  const handleSave = async (e) => {
    e.preventDefault();
    try {
      const createdWork = await createWork(workData);
      const workStepData = { work_id: createdWork.work.id, step_name: "New Work", timestamp: new Date().toISOString(), state: "New Work", status: "Open", };
      await createWorkStep(workStepData);
      selectedProducts.forEach(async (productId) => { await createWorkProduct({ work_id: createdWork.work.id, product_id: productId }); });
      navigate(`/workorders`);
    } catch (error) {
      console.error('Error creating work and workstep:', error);
    }
  };

  const renderDropdown = (label, name, stateData, stateValue, handleChange) => (
    <div className="cwo-form-group">
      <label htmlFor={name}>{label}</label>
      <select name={name} id={name} className="cwo-form-control" value={stateValue} onChange={handleChange}>
        <option value="">Select {label}</option>
        {stateData.map((item) => (
          <option key={item.id} value={item.id}>
            {item.name}
          </option>
        ))}
      </select>
    </div>
  );

  return (
    <div className="create-work-order">
      <h1>Create Work Order</h1>
      <form onSubmit={handleSave}>
        {renderDropdown("Vendor", "vendor_id", vendors, workData.vendor_id, handleChange)}
        {renderDropdown("Customer", "customer_id", customers, workData.customer_id, handleChange)}
        {renderDropdown("Quality Responsible", "quality_responsible_id", qualityResponsibles, workData.quality_responsible_id, handleChange)}
        {renderDropdown("Inspector", "inspector_id", inspectors, workData.inspector_id, handleChange)}
        {renderDropdown("Foreman", "foreman_id", foremen, workData.foreman_id, handleChange)}
        {/* Additional form fields and dropdowns can be added here */}
        <button type="submit">Save</button>
      </form>
    </div>
  );
};

export default CreateWorkOrder;
