import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getFormById } from './formapi';

const segments = [
  { name: 'Sub - Part Dimensiol', order: 1 },
  { name: 'Final Part Measurement', order: 2 },
  { name: 'Paint Report', order: 3 },
  { name: 'Qoating Report', order: 4 },
  { name: 'Product Packing Standart', order: 5 },
  { name: 'Loading Check', order: 6 },
];

const FormEdit = () => {
  const { id } = useParams();
  const [form, setForm] = useState(null);
  const [activeSegment, setActiveSegment] = useState(1);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const formData = await getFormById(id);
        setForm(formData);
      } catch (error) {
        console.error('Error fetching form:', error);
      }
    };

    fetchData();
  }, [id]);

  const createTable = (data) => {
    const table = document.createElement('table');
    table.classList.add('measurement-table');
  
    const thead = table.createTHead();
    const tbody = table.createTBody();
    const headerRow = thead.insertRow();
  
    const headers = [
      'İsim',
      'Teknik Çizim Numarası',
      'Kullanılan Aletler',
      'Açıklama',
      'Gerçek Boyut',
      'Alt Tolerans',
      'Üst Tolerans',
    ];
  
    headers.forEach((header) => {
      const th = document.createElement('th');
      th.textContent = header;
      headerRow.appendChild(th);
    });
  
    data.steps.forEach((step) => {
      step.substeps.forEach((substep) => {
        const row = tbody.insertRow();
        const keys = [
          'name',
          'technical_drawing_numbering',
          'tools',
          'description',
          'actual_dimension',
          'lower_tolerance',
          'upper_tolerance',
        ];
  
        keys.forEach((key) => {
          const cell = row.insertCell();
          const input = document.createElement('input');
          input.type = 'text';
          input.value = substep[key];
          cell.appendChild(input);
        });
      });
    });
  
    return table;
  };

  const handleSegmentClick = (order) => {
    setActiveSegment(order);
  };

  const renderSegmentContent = () => {
    switch (activeSegment) {
      case 1:
        return renderSubPartDimensiol();
      case 2:
        return renderFinalPartMeasurement();
      case 3:
        return renderPaintReport();
      case 4:
        return renderQoatingReport();
      default:
        return null;
    }
  };

  const renderSubPartDimensiol = () => {
    return (
      <div>
        {form.steps[0].substeps.map((substep) => (
          <div key={substep.id}>
            <h3>{substep.name}</h3>
            <p>{substep.description}</p>
            <p>{`Actual dimension: ${substep.actual_dimension}`}</p>
            <p>{`Tolerances: ${substep.lower_tolerance}-${substep.upper_tolerance}`}</p>
            <img src={substep.example_visual_url} alt={substep.name} />
          </div>
        ))}
      </div>
    );
  };

  const renderFinalPartMeasurement = () => {
    return (
      <div>
        <table className="measurement-table">
          <thead>
            <tr>
              <th>Technical Dra. Numbering</th>
              <th>Machine, Device, Tools etc</th>
              <th>Actual Dimension</th>
              <th>Tolerance Value:</th>
              <th>Lower Tolerance</th>
              <th>Upper Tolerance</th>
              <th>Measured Value 1</th>
              <th>Measured Value 2</th>
              <th>Measured Value 3</th>
              <th>OK</th>
              <th>NOK</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>TDN-001</td>
              <td>Machine 1, Tool A</td>
              <td>10</td>
              <td>Tolerance</td>
              <td>9</td>
              <td>11</td>
              <td>9.8</td>
              <td>10.1</td>
              <td>9.9</td>
              <td>✓</td>
              <td></td>
            </tr>
          </tbody>
        </table>
      </div>
    );
  };
  
  const renderPaintReport = () => {
    return (
      <div>
        {form.steps[2].substeps.map((substep) => (
          <div key={substep.id}>
            <h3>{substep.name}</h3>
            <p>{substep.description}</p>
            <p>{`Actual dimension: ${substep.actual_dimension}`}</p>
            <p>{`Tolerances: ${substep.lower_tolerance}-${substep.upper_tolerance}`}</p>
            <img src={substep.example_visual_url} alt={substep.name} />
          </div>
        ))}
      </div>
    );
  };
  
  const renderQoatingReport = () => {
    return (
      <div>
        {form.steps[3].substeps.map((substep) => (
          <div key={substep.id}>
            <h3>{substep.name}</h3>
            <p>{substep.description}</p>
            <p>{`Actual dimension: ${substep.actual_dimension}`}</p>
            <p>{`Tolerances: ${substep.lower_tolerance}-${substep.upper_tolerance}`}</p>
            <img src={substep.example_visual_url} alt={substep.name} />
          </div>
        ))}
      </div>
    );
  };
  return (
    <div>
      <h1>ITP Formu</h1>
      {form ? (
        <div>
          <h2>Product Name: {form.product_name || form.product?.name}</h2>
          <h2>Vendor Name: {form.vendor_name || form.vendor?.name}</h2>
            <div className="segments">
                {segments.map((segment) => (
                <button
                    key={segment.order}
                    onClick={() => handleSegmentClick(segment.order)}
                    className={activeSegment === segment.order ? 'active' : ''}
                >
                    {segment.name}
                </button>
                ))}
            </div>
            <div className="segment-content">{renderSegmentContent()}</div>
        </div>
      ) : (
        <p>Form yükleniyor...</p>
      )}
    </div>
  );
};

export default FormEdit;
