import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import {
  getWorkById,
  getProductById,
  getWorkProducts,
  postQRQuestions,
  createWorkStep,
  updateWorkStepStatus,
} from './worksapi';

const QRControl = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [work, setWork] = useState(null);
  const [productIds, setProductIds] = useState([]);
  const searchParams = new URLSearchParams(location.search);
  const work_id = searchParams.get('work_id');
  const step_id = searchParams.get('step_id');

  useEffect(() => {
    const fetchData = async () => {
      const workData = await getWorkById(work_id);
      setWork(workData);

      const productsData = await getWorkProducts(work_id);

      if (productsData) {
        setProductIds(productsData.data.map(product => product.product_id));
      }
    };

    fetchData();
  }, [work_id]);

  const [products, setProducts] = useState(null);

  useEffect(() => {
    const fetchProducts = async () => {
      const fetchedProducts = await Promise.all(productIds.map(async productId => {
        const product = await getProductById(productId);
        return product.data;
      }));

      setProducts(fetchedProducts);
    };

    if (productIds.length > 0) {
      fetchProducts();
    }
  }, [productIds]);

  return (
    <div className="form-page-container">
      <h2>QR Control</h2>
      {work && (
        <div>
          <h3>Work Details</h3>
          <p>Order Number: {work.data.order_number}</p>
          <p>Project Number: {work.data.project_number}</p>
        </div>
      )}

      {productIds.length > 0 && (
        <div>
          <h3>Product IDs</h3>
          {productIds.map((productId, index) => (
            <p key={index}>product ID: {productId}</p>
          ))}
        </div>
      )}

      {products &&
        products.map((product, productIndex) => (
          <div key={productIndex}>
            <h3>Product: {product.name}</h3>
            <p>Technical Drawing URL: {product.technicaldrawingurl}</p>
          </div>
        ))}

      <button type="button" className="btn btn-primary">
        Save
      </button>
      <button type="button" className="btn btn-success">
        Send
      </button>
      <button type="button" className="btn btn-warning">
        Revize
      </button>
    </div>
  );
};

export default QRControl;
