import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

const QRControl = () => {
  const [checkedBoxes, setCheckedBoxes] = useState(new Array(3).fill(false));
  const [inputValues, setInputValues] = useState(new Array(3).fill(''));
  const navigate = useNavigate();
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);

  const work_id = queryParams.get("work_id");
  const step_id = queryParams.get("step_id");

  const handleCheck = (index) => {
    const newCheckedBoxes = [...checkedBoxes];
    newCheckedBoxes[index] = !checkedBoxes[index];
    setCheckedBoxes(newCheckedBoxes);
  };

  const handleInputChange = (index, value) => {
    const newInputValues = [...inputValues];
    newInputValues[index] = value;
    setInputValues(newInputValues);
  };

  const handleSave = () => {
    // Save işlemini gerçekleştirebilirsiniz
  };

  const handleSend = () => {
    navigate('/qm-control');
  };

  const handleRevise = () => {
    // Revize işlemini gerçekleştirebilirsiniz
  };

  const questions = [
    "Teknik Resim üzerinde müşteri bilgisi yoktur",
    "Ürün malzemesi bulunabilir",
    "Deneme"
  ];

  return (
    <div>
      <h2>QR Control</h2>
      <form>
        {questions.map((question, index) => (
          <div key={index} className="form-group">
            <div className="form-check d-inline-block">
              <input
                type="checkbox"
                className="form-check-input"
                id={`question-${index}`}
                checked={checkedBoxes[index]}
                onChange={() => handleCheck(index)}
              />
            </div>
            <label className="form-check-label d-inline-block" htmlFor={`question-${index}`}>
              {question}
            </label>
            <input
              type="text"
              className="form-control"
              id={`input-${index}`}
              value={inputValues[index]}
              onChange={(e) => handleInputChange(index, e.target.value)}
            />
          </div>
        ))}
        <button onClick={handleSave} className="btn btn-primary">
          Save
        </button>
        <button onClick={handleSend} className="btn btn-success">
          Send
        </button>
        <button onClick={handleRevise} className="btn btn-warning">
          Revize
        </button>
      </form>
    </div>
  );
};

export default QRControl;
