import React from 'react';
import './BarChart.css';

const BarChart = ({ data, width = 800, height = 400 }) => {
  const maxValue = Math.max(...data.map((item) => item.iş));
  const scaleY = height / maxValue;
  const barWidth = width / data.length;

  return (
    <svg className="bar-chart" width={width} height={height}>
      {data.map((item, index) => {
        const barHeight = item.iş * scaleY;
        const x = barWidth * index;
        const y = height - barHeight;
        return (
          <g key={item.name}>
            <rect
              x={x}
              y={y}
              width={barWidth - 10} // Çubuk genişliğini azaltın
              height={barHeight}
              fill="#8884d8"
            />
            <text x={x + 5} y={y - 5} fontSize="12" fill="white"> {/* Beyaz renkte yazdır */}
              {item.iş}
            </text>
            <text x={x + 5} y={height} fontSize="12" dy="-3">
              {item.name}
            </text>
          </g>
        );
      })}
      {/* x ve y ekseni başlıklarını ekleyin */}
      <text x={width / 2} y={height - 5} fontSize="16" textAnchor="middle">
        İşler
      </text>
      <text
        x={5}
        y={height / 2}
        fontSize="16"
        textAnchor="middle"
        transform={`rotate(-90, 5, ${height / 2})`}
      >
        İş Sayısı
      </text>
    </svg>
  );
};

export default BarChart;
