import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import './ProductSegment.css';
import { getFormByVendorIdAndProductId, getFormByFormId } from './worksapi';

const ProductSegment = ({ product, workInfo }) => {
  const location = useLocation();
  const [showContent, setShowContent] = useState(false);
  const [form, setForm] = useState(null);
  const [work, setWork] = useState(null);
  const searchParams = new URLSearchParams(location.search);
  const work_id = searchParams.get('work_id');

  useEffect(() => {
    const fetchData = async () => {
      if (workInfo) {
        const formResponse = await getFormByVendorIdAndProductId(
          workInfo.data.vendor_id,
          product.id
        );
  
        if (formResponse) {
          const formDetails = await getFormByFormId(formResponse.form.id);
          setForm(formDetails);
        }
      }
    };
  
    fetchData();
  }, [product, workInfo]);

  const handleToggleContent = () => {
    setShowContent(!showContent);
  };
  
  const handleToggleSubsteps = (stepIndex) => {
    const updatedSteps = form.steps.map((step, index) => {
      if (index === stepIndex) {
        return {
          ...step,
          showSubsteps: !step.showSubsteps,
        };
      }
      return step;
    });
    setForm({ ...form, steps: updatedSteps });
  };

  return (
<div className="product-segment">
  <button onClick={handleToggleContent} className={showContent ? 'active' : ''}>
    {product.name}
  </button>
  {showContent && (
    <div className="segment-content">
      {form && (
        <div className="steps-container">
          <div className="step-column">
            <h4>Steps</h4>
            {form.steps.map((step) => (
              <div key={step.name} className="step-row">
                <button onClick={() => handleToggleSubsteps(step)}>
                  {step.name}
                </button>
              </div>
            ))}
          </div>
          <div className="substep-column">
            {form.steps.map((step) => (
              <div key={step.name} className="substep-row">
                {step.showSubsteps && (
                  <>
                    <h4>{step.name}</h4>
                    {step.substeps.map((substep) => (
                      <div key={substep.id}>
                        <p>Technical Drawing Numbering: {substep.technical_drawing_numbering}</p>
                        <p>Tools: {substep.tools}</p>
                        <p>Description: {substep.description}</p>
                        <p>Actual Dimension: {substep.actual_dimension}</p>
                        <p>Lower Tolerance: {substep.lower_tolerance}</p>
                        <p>Upper Tolerance: {substep.upper_tolerance}</p>
                        {/* Daha fazla bilgi ekleyebilirsiniz */}
                      </div>
                    ))}
                  </>
                )}
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  )}
</div>

  );
};

export default ProductSegment;
