// client/src/components/Works/QRControl.js
import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { getWorkById } from '../api/workApi';
import { getProductById } from '../api/productApi';

const QRControl = () => {
  const location = useLocation();
  const [work, setWork] = useState(null);
  const [product, setProduct] = useState(null);

  useEffect(() => {
    const searchParams = new URLSearchParams(location.search);
    const work_id = searchParams.get('work_id');
    const step_id = searchParams.get('step_id');

    const fetchData = async () => {
      const workData = await getWorkById(work_id);
      setWork(workData);

      const productData = await getProductById(workData.product_id);
      setProduct(productData);
    };

    fetchData();
  }, [location]);

  return (
    <div>
      <h2>QR Control</h2>
      {work && (
        <div>
          <h3>Work Details</h3>
          <p>Order Number: {work.order_number}</p>
          <p>Project Number: {work.project_number}</p>
          {/* Diğer iş detaylarını istediğiniz şekilde burada gösterebilirsiniz */}
        </div>
      )}

      {product && (
        <div>
          <h3>Product Technical Drawing URL</h3>
          <p>{product.technicaldrawingurl}</p>
        </div>
      )}
    </div>
  );
};

export default QRControl;
