import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { getWorkById, getWorkProducts, createWorkStep, updateWorkStepStatus } from './worksapi';
import './NewWork.css';

const NewWork = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [work, setWork] = useState(null);
  const [products, setProducts] = useState([]);
  const searchParams = new URLSearchParams(location.search);
  const work_id = searchParams.get('work_id');
  const step_id = searchParams.get('step_id');

  useEffect(() => {
    const fetchData = async () => {
      const workData = await getWorkById(work_id);
      setWork(workData);
      const productsData = await getWorkProducts(work_id);
      setProducts(productsData);
    };
    fetchData();
  }, [work_id]);

  const handleSend = async () => {
    if (work) {
      try {
        const workStepData = {
          work_id: work.data.id,
          step_name: 'Certificate',
          timestamp: new Date().toISOString(),
          state: 'Certificate',
          status: 'Open',
        };
        await createWorkStep(workStepData);
        await updateWorkStepStatus(step_id, 'Closed');
        navigate(`/workorders`);
      } catch (error) {
        console.error('Error sending QR questions:', error);
      }
    }
  };

  return (
    <div className="form-page-container">
      <h2 className="form-page-title">QR Control</h2>
      {work && (
        <div className="work-details">
          {/* Render work details only when the work object is defined */}
          {work.data && (
            <>
              <div className="work-detail-row">
                <div className="work-detail">Order Number: {work.data.order_number}</div>
                <div className="work-detail">Project Number: {work.data.project_number}</div>
              </div>
              <div className="work-detail-row">
                <div className="work-detail">Vendor: {work.data.vendor_id}</div>
                <div className="work-detail">Customer: {work.data.customer_id}</div>
              </div>
              <div className="work-detail-row">
                <div className="work-detail">Inspector: {work.data.inspector_id}</div>
                <div className="work-detail">Foreman: {work.data.foreman_id}</div>
              </div>
              <div className="work-detail-row">
                <div className="work-detail">Work Type: {work.data.work_type}</div>
                <div className="work-detail">State: {work.data.state}</div>
              </div>
              <div className="work-detail-row">
                <div className="work-detail">Status: {work.data.status}</div>
                <div className="work-detail">Creator: {work.data.creator_id}</div>
              </div>
            </>
          )}
        </div>
      )}

      <div className="products-container">
        {products &&
          products.map((product, productIndex) => (
            <div key={productIndex} className="product">
              <h3>Product: {product.name}</h3>
              <p>Technical Drawing URL: {product.technicaldrawingurl}</p>
              </div>
          ))}
      </div>

      <button type="button" onClick={handleSend} className="btn btn-success">
        Send
      </button>
    </div>
  );
};

export default NewWork;
