import React, { useState, useEffect } from 'react';
import { Table, Button } from 'reactstrap';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const WorkOrders = () => {
  const [workOrders, setWorkOrders] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetchWorkOrders();
  }, []);

  const fetchWorkOrders = async () => {
    try {
      const response = await axios.get('YOUR_API_URL_HERE');
      setWorkOrders(response.data);
    } catch (error) {
      console.error('Work orders alınamadı:', error);
    }
  };

  const handleEdit = (id) => {
    navigate(`/work-orders/${id}`);
  };

  return (
    <div>
      <Button color="primary" className="float-right mb-3" onClick={() => navigate('/create-work-order')}>
        + Create
      </Button>
      <Table striped>
        <thead>
          <tr>
            <th>ID</th>
            <th>Title</th>
            <th>Description</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {workOrders.map((workOrder) => (
            <tr key={workOrder.id}>
              <td>{workOrder.id}</td>
              <td>{workOrder.title}</td>
              <td>{workOrder.description}</td>
              <td>
                <Button color="warning" onClick={() => handleEdit(workOrder.id)}>
                  Düzenle
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

export default WorkOrders;
