import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getFormById } from './formapi';

const segments = [
  { name: 'Sub - Part Dimensiol', order: 1 },
  { name: 'Final Part Measurement', order: 2 },
  { name: 'Paint Report', order: 3 },
  { name: 'Qoating Report', order: 4 },
  { name: 'Qoating Report', order: 5 },
  { name: 'Product Packing Standart', order: 6 },
  { name: 'Loading Check', order: 7 },
];

const FormEdit = () => {
  const { id } = useParams();
  const [form, setForm] = useState(null);
  const [segments, setSegments] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
        try {
          const formData = await getFormById(id);
          setForm(formData);
          setSegments(formData.steps);
        } catch (error) {
          console.error('Error fetching form:', error);
      }
    };

    fetchData();
  }, [id]);

  const handleSegmentClick = (order) => {
    setActiveSegment(order);
  };

  const renderSegmentContent = () => {
    switch (activeSegment) {
        case 1:
          return (
            <div>
              {segment.substeps.map((substep) => (
                <div key={substep.id}>
                  <h3>{substep.name}</h3>
                  <p>{substep.description}</p>
                  {/* Diğer alanları da burada görüntüleyin. */}
                </div>
              ))}
            </div>
          );
      case 2:
        return <div>Final Part Measurement content</div>;
      case 3:
        return <div>Paint Report content</div>;
      case 4:
        return <div>Qoating Report content</div>;
      case 5:
        return <div>Qoating Report content</div>;
      case 6:
        return <div>Product Packing Standart content</div>;
      case 7:
        return <div>Loading Check content</div>;
      default:
        return null;
    }
  };

  return (
    <div>
      <h1>ITP Formu</h1>
      {form ? (
        <div>
          <h2>Product Name: {form.product_name}</h2>
          <h2>Vendor Name: {form.vendor_name}</h2>
          <div className="segments">
            {segments.map((segment) => (
              <button
                key={segment.order}
                onClick={() => handleSegmentClick(segment.order)}
                className={activeSegment === segment.order ? 'active' : ''}
              >
                {segment.name}
              </button>
            ))}
          </div>
          <div className="segment-content">{renderSegmentContent()}</div>
        </div>
      ) : (
        <p>Form yükleniyor...</p>
      )}
    </div>
  );
};

export default FormEdit;
