import React, { useState } from 'react';
import './FormsPage';

const FormsPage = () => {
  const [formData, setFormData] = useState({
    product_id: '',
    vendor_id: '',
    // Diğer form alanlarını burada tanımlayın
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Form verilerini sunucuya gönderme işlemini burada gerçekleştirin
    console.log(formData);
  };

  return (
    <div className="form-page-container">
      <h2>Form Oluştur</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="product_id">Ürün ID:</label>
          <input
            type="text"
            id="product_id"
            name="product_id"
            value={formData.product_id}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="vendor_id">Satıcı ID:</label>
          <input
            type="text"
            id="vendor_id"
            name="vendor_id"
            value={formData.vendor_id}
            onChange={handleChange}
          />
        </div>
        {/* Diğer form alanlarını burada ekleyin */}
        <button type="submit">Formu Gönder</button>
      </form>
    </div>
  );
};

export default FormsPage;
