import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const Users = () => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    // Burada API isteği yaparak kullanıcıları alın
    fetch('/api/allusers')
      .then((res) => res.json())
      .then((data) => setUsers(data.data))
      .catch((error) => console.error('API isteğinde hata:', error));
  }, []);
  

  return (
    <div>
      <h1>Kullanıcılar</h1>
      <Link to="/create-user">Kullanıcı Oluştur</Link>
      <div>
        {users.map((user) => (
          <div key={user.data.id}>
            <span>{user.data.id}</span>
            <span>{user.data.email}</span>
            <span>{user.data.phone}</span>
            <span>{user.data.role}</span>
            <span>{user.data.name}</span>
            <span>{user.data.username}</span>
            <span>{user.data.related_company}</span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Users;
